require_once( JApplicationHelper::getPath( 'toolbar_html' ) );
switch ($task) {case 'updates':    TOOLBAR_jpackage::_UPDATES();    break;case 'showUpdates':    TOOLBAR_jpackage::_SHOW_UPDATES();    break;case 'showUpdateInstall':    TOOLBAR_jpackage::_UPDATE_INSTALL();    break;case 'browse':    TOOLBAR_jpackage::_BROWSE();    break;case 'showAddSite':    TOOLBAR_jpackage::_ADD_SITE();    break;case 'showEditSite':    TOOLBAR_jpackage::_EDIT_SITE();    break;case 'category':    TOOLBAR_jpackage::_CATEGORY();    break;case 'package':    TOOLBAR_jpackage::_PACKAGE();    break;case 'install';    TOOLBAR_jpackage::_INSTALL();    break;default:    TOOLBAR_jpackage::_DEFAULT();    break;
}
