/*** Function that handles the installation tasks.*/
function com_install() {  global $database;
  # Show installation result to user  ?>	<center>	<table width="100%" border="0">	<tr>		<td background="E0E0E0" style="border:1px solid #999;" colspan="2">		<code>Installation Process:<br />		<?php		# Set up new icons for admin menu		$database->setQuery("UPDATE #__components SET admin_menu_img='js/ThemeOffice/edit.png' WHERE admin_menu_link='option=com_extensionsdir_directory'");		$iconresult[0] = $database->query();		foreach ($iconresult as $i=>$icresult) {			if ($icresult) {				echo "<font color='green'>FINISHED:</font> Image of menu entry $i has been corrected.<br />";			} else {				echo "<font color='red'>ERROR:</font> Image of menu entry $i could not be corrected.<br />";			} # End if		} # End foreach		?>		<br><br>		<font color="green"><b>Installation finished.</b></font></code>		</td>	</tr>  </table>  </center>  <?php} # End function com_install?>
