/** * @package 	Joomla * @subpackage	J!Package Directory * @since 		1.5 */class TOOLBAR_jpackagedir {	/**	* Draws the default menu header, package overview.	*/	function _DEFAULT()	{		JMenuBar::title( JText::_( 'J!Package Directory Packages'), 'addedit.png' );		JMenuBar::publishList('publish');		JMenuBar::unpublishList('unpublish');		JMenuBar::deleteList();		JMenuBar::editList();		JMenuBar::addNew(); 		// Get the toolbar object instance and add a popup configuration button 		$bar = & JToolBar::getInstance('JComponent');		$bar->appendButton( 'Popup', 'config', 'Configuration', 'index3.php?option=com_config&c=component&component=com_media', '570', '150' );	}	/**	* Draws the menu header when a user edits a directory entry.	*/
	function _EDIT() {		global $task;		if ($task == 'new') {			JMenuBar::title( JText::_( 'Edit Directory Entry'), 'addedit.png' );		} else {			JMenuBar::title( JText::_( 'Edit Directory Entry'), 'addedit.png' );					}		JMenuBar::save();		JMenuBar::cancel();	}	/**	* Draws the menu header for projects overview	*/	function _PROJECTS() {		JMenuBar::title( JText::_( 'J!Package Projects'), 'addedit.png' );		JMenuBar::publishList('publishproject');		JMenuBar::unpublishList('unpublishproject');		JMenuBar::deleteList('deleteproject', 'deleteproject');		JMenuBar::editList('editproject');		JMenuBar::addNew('newproject');  		// Get the toolbar object instance and add a popup configuration button 		$bar = & JToolBar::getInstance('JComponent');		$bar->appendButton( 'Popup', 'config', 'Configuration', 'index3.php?option=com_config&c=component&component=com_media', '570', '150' );	}	/**	* Draws the menu header when a user edits a project entry.	*/	function _EDITPROJECT() {		global $task;		if ($task == 'newproject') {			JMenuBar::title( JText::_( 'New Project Entry'), 'addedit.png' );					} else {			JMenuBar::title( JText::_( 'Edit Project Entry'), 'addedit.png' );					}		JMenuBar::save('saveproject');		JMenuBar::cancel('cancelproject');	}}
?>
